// AulaScene.js
import React, { useRef, useEffect, useState } from "react";
import { Canvas } from "@react-three/fiber";
import { useSocketManager } from "./SocketManager";
import Ball from "./Ball";

const AulaScene = () => {
  const ref = useRef();
  const [userCount, setUserCount] = useState(0);

  const handleNewUser = () => {
    setUserCount((prevCount) => prevCount + 1);
  };

  useEffect(() => {
    window.addEventListener("storage", handleNewUser);
    return () => {
      window.removeEventListener("storage", handleNewUser);
    };
  }, []);

  return (
    <Canvas
      camera={{ position: [0, 2.5, 2.5] }}
      onCreated={({ camera }) => camera.lookAt(0, 1, 0)}
    >
      <gridHelper ref={ref} args={[100, 100]} />

      {[...Array(userCount)].map((_, index) => (
        <Ball key={index} userId={index + 1} floor={ref} />
      ))}
    </Canvas>
  );
};

export default AulaScene;
