// AulaScene.js
import React, { useRef, useEffect, useState } from "react";
import { Canvas } from "@react-three/fiber";
import { useSocketManager } from "./SocketManager";
import Ball from "./Ball";

const AulaScene = () => {
  const ref = useRef();
  const [userCount, setUserCount] = useState(0);
  const { socket, characters } = useSocketManager();

  useEffect(() => {
    socket.emit("sceneLoaded");

    socket.on("userConnected", () => {
      console.log("User connected");
      setUserCount((prevCount) => prevCount + 1);
    });

    socket.on("userDisconnected", () => {
      console.log("User disconnected");
      setUserCount((prevCount) => prevCount - 1);
    });

    return () => {
      socket.off("userConnected");
      socket.off("userDisconnected");
    };
  }, [socket]);

  return (
    <Canvas
      camera={{ position: [0, 2.5, 2.5] }}
      onCreated={({ camera }) => camera.lookAt(0, 1, 0)}
    >
      <gridHelper ref={ref} args={[100, 100]} />

      {characters.map((character) => (
        <Ball key={character.id} userId={character.id} floor={ref} />
      ))}
    </Canvas>
  );
};

export default AulaScene;
