import React, { useRef, useEffect, useState, useMemo } from "react";
import { createRoot } from "react-dom/client";
import { Canvas, useFrame } from "@react-three/fiber";
import { Stats } from "@react-three/drei";
import { Vector3, Quaternion } from "three";
import { socket } from "./SocketManager";

const AulaScene = () => {
  const ref = useRef();
  const [userCount, setUserCount] = useState(0);

  const handleNewUser = () => {
    setUserCount((prevCount) => prevCount + 1);
  };

  useEffect(() => {
    window.addEventListener("storage", handleNewUser);
    return () => {
      window.removeEventListener("storage", handleNewUser);
    };
  }, []); // Se ejecuta una vez al montar la escena

  function useKeyboard() {
    const keyMap = useRef({});

    useEffect(() => {
      const onDocumentKey = (e) => {
        keyMap.current[e.code] = e.type === "keydown";
      };
      document.addEventListener("keydown", onDocumentKey);
      document.addEventListener("keyup", onDocumentKey);
      return () => {
        document.removeEventListener("keydown", onDocumentKey);
        document.removeEventListener("keyup", onDocumentKey);
      };
    });

    return keyMap.current;
  }

  function Ball({ userId, floor }) {
    const ref = useRef();
    const keyMap = useKeyboard();
    const [socket, setSocket] = useState(null);

    const v = useMemo(() => new Vector3(), []);
    const q = useMemo(() => new Quaternion(), []);
    const angularVelocity = useMemo(() => new Vector3(), []);

    useEffect(() => {
      // Crear una nueva instancia de socket.io-client para esta bola (usuario)
      const newSocket = io("http://localhost:3001");
      setSocket(newSocket);

      // Manejar eventos específicos para esta bola (usuario)
      const handleConnect = () => {
        console.log(`User ${userId} connected to server`);
      };

      const handleDisconnect = () => {
        console.log(`User ${userId} disconnected from server`);
      };

      // Agregar listeners para eventos específicos de esta bola (usuario)
      newSocket.on("connect", handleConnect);
      newSocket.on("disconnect", handleDisconnect);

      // Limpieza al desmontar la bola (usuario)
      return () => {
        newSocket.off("connect", handleConnect);
        newSocket.off("disconnect", handleDisconnect);
        newSocket.disconnect(); // Desconectar al desmontar
      };
    }, [userId]); // Se ejecuta cada vez que cambia el userId

    useFrame((_, delta) => {
      keyMap["KeyW"] && (angularVelocity.x -= delta * 5);
      keyMap["KeyS"] && (angularVelocity.x += delta * 5);
      keyMap["KeyA"] && (angularVelocity.z += delta * 5);
      keyMap["KeyD"] && (angularVelocity.z -= delta * 5);

      q.setFromAxisAngle(angularVelocity, delta).normalize();
      ref.current.applyQuaternion(q);

      angularVelocity.lerp(v, 0.01); // slow down the roll

      floor.current.position.x += angularVelocity.z * delta;
      floor.current.position.z -= angularVelocity.x * delta;

      floor.current.position.x = floor.current.position.x % 10;
      floor.current.position.z = floor.current.position.z % 10;
    });

    return (
      <mesh ref={ref} position-y={1.0}>
        <sphereGeometry />
        <meshNormalMaterial wireframe />
      </mesh>
    );
  }

  return (
    <>
      <Canvas
        camera={{ position: [0, 2.5, 2.5] }}
        onCreated={({ camera }) => camera.lookAt(0, 1, 0)}
      >
        <gridHelper ref={ref} args={[100, 100]} />
        <Ball floor={ref} />
      </Canvas>
    </>
  );
};

export default AulaScene;
